# 1. Création du hash associant chaque crypto à son prix (conversion en Float)
my_hash = cryptos.zip(prices.map(&:to_f)).to_h

# 2 & 3. Recherche des cryptos avec les valeurs les plus élevées et les plus basses
max_price = -Float::INFINITY
min_price = Float::INFINITY

highest_cryptos = []
lowest_cryptos = []

my_hash.each do |name, price|
  if price > max_price
    max_price = price
    highest_cryptos = [name]
  elsif price == max_price
    highest_cryptos << name
  end

  if price < min_price
    min_price = price
    lowest_cryptos = [name]
  elsif price == min_price
    lowest_cryptos << name
  end
end

# 4. Comptage des cryptos contenant 'coin' (insensible à la casse)
coin_count = my_hash.keys.count { |name| name.downcase.include?('coin') }

# 5. Sélection des cryptos dont le prix est inférieur à 6000
under_6000 = my_hash.select { |_name, price| price < 6000 }
under_6000_names = under_6000.keys

# 6. Recherche de la crypto la plus chère parmi celles à moins de 6000
top_under_6000_price = under_6000.values.max
top_under_6000 = under_6000.select { |_name, price| price == top_under_6000_price }.keys

# 7. Affichage des résultats
puts "Crypto(s) avec le prix le plus élevé (#{max_price}) : #{highest_cryptos.join(', ')}"
puts "Crypto(s) avec le prix le plus bas (#{min_price}) : #{lowest_cryptos.join(', ')}"
puts "Nombre de cryptos contenant 'coin' : #{coin_count}"
puts "Cryptos dont le prix < 6000 : #{under_6000_names.join(', ')}"
puts "Crypto la plus chère parmi celles < 6000 : #{top_under_6000.join(', ')} (#{top_under_6000_price})"